const soundFile = "upload-player/feanor/misty-step.wav";
const videoFiles = {
	vanish: "jb2a.misty_step.01.blue",
	appear: "jb2a.misty_step.02.blue",
	range: "jb2a.extras.tmfx.border.circle.simple.01"
};
await Sequencer.Preloader.preloadForClients([soundFile, ...Object.values(videoFiles)]);

if ( args[0] === "each" || args[0] === "off" ) {
	const lastArg = args[args.length - 1];
	const sourceActor = MidiQOL.MQfromActorUuid(lastArg.actorUuid);
	const sourceToken = MidiQOL.MQfromUuid(lastArg.tokenUuid).object;
	const effect = sourceActor.effects.get(lastArg.effectId);

	if ( args[0] === "each" && lastArg.efData.flags.dae.macroRepeat === "endEveryTurn" ) {
		const itemData = lastArg.efData.flags.dae.itemData;
		const roll = await new Roll("d20").evaluate({async: true});
		roll.toMessage({
			speaker: ChatMessage.getSpeaker({actor: sourceActor}),
			flavor: `<div class="dnd5e chat-card"><header class="card-header" style="display:flex">
				<img src=${itemData.img} width="36" height="36"><h3>${itemData.name} roll</h3>
			</header></div>`
		});
		if ( roll.total >= 11 ) {
			await effect.setFlag("dae", "macroRepeat", "startEveryTurn");
			vanish(sourceToken);
			return;
		}
	}

	if ( lastArg.efData.flags.dae.macroRepeat === "startEveryTurn" ) {
		if ( effect ) await effect.setFlag("dae", "macroRepeat", "endEveryTurn");
		while ( sourceToken.data.alpha < 1 ) {
			ui.notifications.info("Waiting for the vanish function to finish.");
			await Sequencer.Helpers.wait(3000);
		}
		chooseDestination(sourceToken);
	}
}

function vanish(token) {
	new Sequence()
		.sound()
			.file(soundFile)
		.wait(2000)
		.effect()
			.file(videoFiles.vanish)
			.atLocation(token)
			.size(token.data.width * 2, {gridUnits: true})
			.randomRotation()
		.wait(1000)
		.animation()
			.on(token)
			.opacity(0)
		.thenDo(() => {TokenMagic.addFilters(token, "spectral-body")})
		.wait(10000)
		.animation()
			.on(token)
			.fadeIn(5000)
		.play();
}

function chooseDestination(token) {
	new Sequence()
		.animation()
			.on(token)
			.fadeOut(2000)
		.effect()
			.file(videoFiles.range)
			.size(token.data.width + 4.3, {gridUnits: true})
			.atLocation(token)
			.belowTokens()
			.fadeIn(2000)
			.fadeOut(2000)
			.persist(true)
			.name("range-template")
		.wait(2000)
		.thenDo(() => {TokenMagic.deleteFilters(token)})
		.play();

	canvas.app.stage.addListener("pointerdown", event => {
		const position = event.data.getLocalPosition(canvas.app.stage);
		const topLeft = canvas.grid.getTopLeft(position.x, position.y);
		const fakeToken = {data: {x: topLeft[0], y: topLeft[1], width: 1, height: 1, elevation: token.data.elevation}};
		const distance = MidiQOL.getDistance(token, fakeToken);
		if ( event.data.button === 0 ) { // Left click
			if ( distance > 10 ) {
				return ui.notifications.error("The selected spot is out of range. Choose again or middle click to cancel.");
			}
			Sequencer.EffectManager.endEffects({name: "range-template"});
			canvas.app.stage.removeListener("pointerdown");
			appear(token, topLeft);
		} else if ( event.data.button === 1 ) { // Middle click
			Sequencer.EffectManager.endEffects({name: "range-template"});
			canvas.app.stage.removeListener("pointerdown");
			appear(token);
		}
	});
}

