const overrideData = {
	actorLink: false,
	displayBars: 0,
	displayName: 0,
	disposition: 0,
	flags: {warpgate: {casterActorId: actor.id}},
	height: 1,
	name: item.name,
  texture: {scaleX: 0.5, scaleY: 0.5, src: handFile},
	sight: {enabled: false},
	width: 1
};
const tokenData = await dummyActor.getTokenDocument(overrideData);

warpgate.crosshairs.show({
	icon: handFile,
	interval: -1,
	rememberControlled: true
},{
	show: checkDistance
});

async function checkDistance(crosshairs) {
	const range = item.system.range.value;
	const unit = canvas.scene.grid.units;
	let crosshairsDistance = 0, counter = 0;
	if ( token ) {
		while ( crosshairs.inFlight && counter < 600 ) {
			counter++;
			await warpgate.wait(100);	// Wait for initial render and delay loop
			const ray = new Ray(token.center, crosshairs);
			const distance = canvas.grid.measureDistances([{ray}], {gridSpaces: true});
			if ( distance !== crosshairsDistance ) {
				crosshairsDistance = distance;
				crosshairs.label = `${distance} ${unit}`;
				if ( distance > range ) crosshairs.icon = "icons/svg/hazard.svg";
				else crosshairs.icon = handFile;
				crosshairs.draw();
			}
		}
	} else {
		while ( crosshairs.inFlight && counter < 600 ) {
			counter++;
			await warpgate.wait(100);
		}
	}
	if ( counter >= 600 ) crosshairs.inFlight = false;
	if ( crosshairs.cancelled ) return;
	if ( crosshairsDistance > range ) return ui.notifications.warn("The location is out of spell range.");
	await new Sequence()
		.sound("upload-player/feanor/misty-step.wav")
			.fadeInAudio(2000)
			.fadeOutAudio(3000)
		.wait(300)
		.effect()
			.file("jb2a.swirling_sparkles.01.blue")
			.atLocation(crosshairs, {cacheLocation: true})
			.scale(0.5)
		.wait(2400)
		.play();
	const mageHandToken = canvas.tokens.placeables.find(t => t.document.flags.warpgate?.casterActorId === actor.id);
	if ( mageHandToken ) warpgate.dismiss(mageHandToken.id);
	warpgate.spawnAt(crosshairs, tokenData);
}

