// Prompts the user to choose how many charges to expend
async function selectCharges() {
	return new Promise((resolve, reject) => {
		const chargesLeft = item.system.uses.value;
		const title = "Wand of Magic Missiles: Usage Configuration";
		let content = '<p style="text-align:center">No charges left to expend</p>';
		let buttons = {};
		if ( chargesLeft ) {
			let options = "";
			for ( let i = 1; i <= chargesLeft; i++ ) options += `<option value=${i}>${i}</option>`;
			content = `<form style="text-align:center">
				<label>Charges to expend:</label>
				<select style="width:50px; margin:0 0 5px 10px">${options}</select>
			</form>`;
			buttons.cast = {
				// Update to fontawesome v6 on Foundry v10: icon: '<i class="fas fa-wand-sparkles"></i>',
				icon: '<i class="fas fa-magic"></i>',
				label: "Cast",
				callback: html => resolve(html[0].querySelector("select").value)
			};
		}
		new Dialog({
			title, content, buttons,
			default: chargesLeft ? "cast" : null,
			render: html => html.find("select").focus(),
			close: () => reject(new Error("The Dialog was closed without a choice being made."))
		},{top: 0, width: 350}).render(true);
	});
}

