// Prompts the user to choose how many missiles to assign to which targets
async function setTargets() {
	return new Promise((resolve, reject) => {
		new Dialog({
			title: "Wand of Magic Missiles: Usage Configuration",
			content: `<p style="text-align:center">Select your targets | Missiles left: <b>${missilesLeft}</b></p>`,
			buttons: {
				one: {
					icon: '<i class="fas fa-check"></i>',
					label: "1 missile",
					callback: () => resolve(aimMissiles(1))
				},
				two: {
					icon: '<i class="fas fa-check-double"></i>',
					label: "2 missiles",
					callback: () => resolve(aimMissiles(2))
				},
				all: {
					icon: '<i class="fas fa-bullseye"></i>',
					label: "All missiles",
					callback: () => resolve(aimMissiles("all"))
				}
			},
			default: "all",
			render: html => html.find("button.all").focus(),
			close: () => reject(new Error("Wand of Magic Missiles: The Dialog was closed without being accepted"))
		},{top: 0, width: 350}).render(true);
	});
}

// Validates and saves the missile aiming choices of the user
function aimMissiles(quantity) {
	const targets = Array.from(game.user.targets);
	if ( !targets.length ) return ui.notifications.warn("Choose at least one target.");
	if ( token ) {
		const range = item.system.range.value;
		for ( const target of targets ) {
			const distance = MidiQOL.getDistance(token, target);
			if ( distance > range ) return ui.notifications.warn(`One target is ${distance} units away and your range is only ${range}.`);
		}
	}
	const missiles = quantity === "all" ? Math.floor(missilesLeft / targets.length) : quantity;
	if ( !missiles || missiles * targets.length > missilesLeft ) {
		return ui.notifications.warn("Not enough missiles, you must choose another setup.");
	} else {
		missilesLeft -= missiles * targets.length;
		for ( const target of targets ) {
			const targetUuid = target.document.uuid;
			if ( !(targetUuid in aimedMissiles) ) aimedMissiles[targetUuid] = missiles;
			else aimedMissiles[targetUuid] += missiles;
		}
	}
}

// Prompts the user to choose whether or not to use Empowered Spell metamagic
async function confirmEmpoweredSpell() {
	const empoweredSpell = actor.items.getName("Metamagic: Empowered Spell");
	const sorceryPoints = actor.items.getName("Sorcery Points");
	const sorceryPointsLeft = sorceryPoints?.system.uses.value;
	if ( !empoweredSpell || !sorceryPointsLeft ) return false;
	return Dialog.confirm({
		title: "Empowered Spell: Confirmation Dialog",
		content: '<p style="text-align:center">Spend 1 sorcery point to reroll the damage?</p>',
		yes: () => {
			sorceryPoints.update({"system.uses.value": sorceryPointsLeft - 1});
			empoweredSpell.displayCard();
			return new Roll("1d4+1");
		},
		defaultYes: false,
		render: html => html.find("button.no").focus(),
		options: {top: 0, width: 350}
	});
}

