// Rolls damage for each missile and plays sound and visual effects
async function castMissiles(saveMode, fxMode) {
	let dmgTargets = Object.keys(aimedMissiles);
	let aimedMissilesClone = {...aimedMissiles}; // Clones the object using spread syntax (for nested data, use deepClone() instead)
	let fxTargets = Object.keys(aimedMissilesClone);
	let spell;

	// Rolls one Concentration saving throw for each missile
	// RAI: https://twitter.com/jeremyecrawford/status/716012166101401600
	if ( saveMode === "multiConcentrationSaves" ) {
		spell = createSpell(damageRoll.total);
		while ( dmgTargets.length ) {
			await MidiQOL.completeItemRoll(spell, {targetUuids: dmgTargets});
			for ( const target in aimedMissiles ) {
				aimedMissiles[target]--;
				if ( !aimedMissiles[target] ) delete aimedMissiles[target];
			}
			dmgTargets = Object.keys(aimedMissiles);
		}
	}

	// Rolls one Concentration saving throw for each target (house rule)
	// Groups targets according to how many missiles were aimed at each of them
	if ( saveMode === "singleConcentrationSave" ) {
		const missilesPerTarget = Object.values(aimedMissiles);
		const maxMissilesPerTarget = Math.max(...missilesPerTarget);
		for ( let missiles = 1; missiles <= maxMissilesPerTarget; missiles++ ) {
			const midiTargets = dmgTargets.filter(target => aimedMissiles[target] === missiles);
			if ( !midiTargets.length ) continue;
			spell = createSpell(damageRoll.total * missiles);
			await MidiQOL.completeItemRoll(spell, {targetUuids: midiTargets});
		}
	}

	// Strikes missiles simultaneously at all targets
	// RAW, but less visually appealing with the effects available
	if ( fxMode === "strikeSimultaneously" ) {
		const seq = new Sequence();
		const missilesNumber = Object.values(aimedMissilesClone).reduce((acc, value) => acc + value, 0);
		for ( const target of fxTargets ) {
			seq.effect()
				.file("jb2a.magic_missile.purple")
				.atLocation(token)
				.stretchTo(target)
				.randomizeMirrorY()
				.repeats(aimedMissilesClone[target]);
		}
		seq.sound()
			.file("upload-player/feanor/magic-missile/*.mp3")
			.repeats(missilesNumber, 150);
		await seq.play();
	}

	// Strikes missiles successively at random targets
	// Not RAW, just for fun, with no mechanical consequence
	if ( fxMode === "strikeSuccessively" ) {
		while ( fxTargets.length ) {
			const target = fxTargets[Math.floor(Math.random() * fxTargets.length)];
			await new Sequence()
				.effect()
					.file("jb2a.magic_missile.purple")
					.atLocation(token)
					.stretchTo(target)
					.randomizeMirrorY()
				.sound()
					.file("upload-player/feanor/magic-missile/*.mp3")
				.wait(300)
				.play();
			aimedMissilesClone[target]--;
			if ( !aimedMissilesClone[target] ) delete aimedMissilesClone[target];
			fxTargets = Object.keys(aimedMissilesClone);
		}
	}
}

